import React from 'react';
import { Box, Button, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';


const HomePage: React.FC = () => {
    const navigate = useNavigate();

    const doButtonClick = () => {
        navigate('/photos');
    };

    return (
      <>
        <Box
          sx={{
            height: '120px',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            textAlign: 'center',
            background: 'linear-gradient(135deg, rgba(255, 0, 0, 0.9) 0%, rgba(200, 0, 0, 0.85) 50%, rgba(139, 0, 0, 1) 100%)',
            backdropFilter: 'blur(10px)',
          }}
        >
          <Typography variant="h6" fontSize={"24px"} color='white'>
            Welcome to depop<i>Dash</i>.
          </Typography>
        </Box>
        <Box
          sx={{
            display: 'flex',
            flexDirection: 'row',
            alignItems: 'center',
            justifyContent: 'center',
            height: '650px',
            width: '1800px',
            margin: '0 auto',
            border: '2px solid blue'
          }}
        >
          <Box
            sx={{
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              textAlign: 'left',
              width: '50%',
              height: '100%',
              padding: '0 50px 0 50px',
              gap: 3,
            }}
          >
            <Typography variant="h6" fontSize="40px" color="text.secondary">
              <b>Save time editing product photos.</b>
            </Typography>
            <Typography variant="h6" fontSize="17px" color="text.secondary">
              depop<i>Dash</i> is an AI-powered tool that allows sellers to automate editing product photos for marketplaces such as Depop.
            </Typography>
            <Typography variant="h6" fontSize="17px" color="text.secondary">
              Effortlessly refine your Depop listings to boost sales & engagement. Developed by a Depop Top Seller.
            </Typography>
            <Button
              variant="contained"
              sx={{
                paddingX: 3, // Adds horizontal padding to the button
                paddingY: 1, // Adds vertical padding to the button
                width: '100%', // Ensures the button only takes up the space it needs
                backgroundColor: 'primary',
                color: 'black',
                ':hover': {
                  backgroundColor: '#ff4040',
                },
                mt: 3
              }}
              onClick={doButtonClick}
            >
              <Typography variant="h6" fontSize="16px" color="white">
                Try it now
              </Typography>
            </Button>
          </Box>
          <Box
            sx={{
              width: '50%',
              height: '100%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              overflow: 'hidden',
            }}
          >
            <img
                src='/images/depop_products.webp'
                alt='Depop Logo'
                style={{
                  width: '800px',
                  height: '525px',
                  borderRadius: '12px',
                  objectFit: 'fill',
                }}
              />
          </Box>
        </Box>
        <Box
          sx={{
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            textAlign: 'center',
            width: '100%',
            height: '100%',
            padding: '0 50px 0 50px',
            gap: 3,
            border: '2px solid blue'
          }}
        >
          <Typography variant="h6" fontSize="40px" color="text.secondary">
            <b>How it works.</b>
          </Typography>
        </Box>
      </>
    );
}

export default HomePage;
