import Box from '@mui/material/Box';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const navItems = [
  {
    label: 'Product Tracking',
    path: '/products',
  },
  {
    label: 'Price Tracking',
    path: '/prices',
  },
  {
    label: 'My Engagement',
    path: '/engagement',
  },
];

export const Header: React.FC = () => {
  const location = useLocation(); // useLocation returns a location object
  const currentTab = location.pathname; // Get the current pathname

  return (
    <Box
      component={'nav'}
      sx={{ borderBottom: 1, borderBottomColor: 'rgba(0, 0, 0, 0.12)' }}
    >
      <Box
        sx={{
          display: 'flex',
          alignItems: 'center', // Corrected from alignContent to alignItems
          textDecoration: 'none',
        }}
        component={Link}
        to="/home"
      >
        <p>Depop Dash</p>
        <Tabs value={currentTab}>
          {navItems.map(({ label, path }) => (
            <Tab
              key={label}
              label={label}
              value={path}
              to={path}
              component={Link}
              sx={{ typography: 'h6', color: 'text.secondary' }} // Fixed typo from 'tex.secondary'
            />
          ))}
        </Tabs>
      </Box>
    </Box>
  );
};
