// Header.jsx
import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';

// Styled components using the theme
const HeaderContainer = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 16px 32px;
  background-color: ${({ theme }) => theme.colors.primary};
  color: ${({ theme }) => theme.colors.textSecondary};
`;

const NavLinks = styled.nav`
  display: flex;
  gap: 20px;
`;

const NavLink = styled(Link)`
  color: ${({ theme }) => theme.colors.textSecondary};
  text-decoration: none;
  font-weight: bold;

  &.active {
    border-bottom: 2px solid ${({ theme }) => theme.colors.secondary};
  }
`;

const LocationInfo = styled.div`
  color: ${({ theme }) => theme.colors.textSecondary};
  font-size: 0.9rem;
`;

const SearchBar = styled.input`
  padding: 8px;
  border: 1px solid ${({ theme }) => theme.colors.secondary};
  border-radius: 4px;
  background-color: ${({ theme }) => theme.colors.background};
  color: ${({ theme }) => theme.colors.textPrimary};
`;

const Header = () => {
  const location = useLocation();

  return (
    <HeaderContainer>
      <NavLinks>
        <NavLink to="/" className={location.pathname === '/' ? 'active' : ''}>
          Home
        </NavLink>
        <NavLink to="/trends" className={location.pathname === '/trends' ? 'active' : ''}>
          Trends
        </NavLink>
        <NavLink to="/analytics" className={location.pathname === '/analytics' ? 'active' : ''}>
          Analytics
        </NavLink>
        <NavLink to="/price-tracker" className={location.pathname === '/price-tracker' ? 'active' : ''}>
          Price Tracker
        </NavLink>
      </NavLinks>
      <SearchBar type="text" placeholder="Search..." />
      <LocationInfo>{`Current Path: ${location.pathname}`}</LocationInfo>
    </HeaderContainer>
  );
};

export default Header;
